import './Home.css';
class Home extends Component {
    constructor(props){
        //Call the constrictor of Super class i.e The Component
    super(props);
        //maintain the state required for this component
    this.state = {
        location : "",
        startdate : "",
        enddate : ""
    }
        //Bind the handlers to this class
    this.locationChangeHandler = this.locationChangeHandler.bind(this);
    this.startdateChangeHandler = this.startdateChangeHandler.bind(this);
    this.enddateChangeHandler = this.enddateChangeHandler.bind(this);
    this.submitSearch = this.submitSearch.bind(this);
    }
    locationChangeHandler = (e) => {
        this.setState({
            location : e.target.value
        })
    }
    startdateChangeHandler = (e) => {
        this.setState({
            startdate : e.target.value
        })
    }
    enddateChangeHandler = (e) => {
        this.setState({
            enddate : e.target.value
        })
    }
    submitSearch = (e) => {
        var headers = new Headers();

        e.preventDefault();

            const values = {
                emailtopass : jwt_decode(localStorage.getItem("travelerJWT")).email,
                location : this.state.location,
                startdate : this.state.startdate,
                enddate : this.state.enddate,
                guests : this.state.guests,
            }


            this.props.propsearch(values, () => {

               // this.props.history.push("/traveler/propertysearch");

              });



    }
    render() { 
        return ( 
            <div className="bgnd">
                <form class="form-inline5" onSubmit={this.submitSearch}>
                    <input onChange = {this.locationChangeHandler} className="txt sizes robutton1" type="text"  name="location" tabindex="1" placeholder="Where do you want to go?" required></input>
           &nbsp;&nbsp;&nbsp;&nbsp;
                    <input onChange = {this.startdateChangeHandler} type="date" className="txt5 sizes robutton1" name="startdate" placeholder="MM/DD/YYY" required/>&nbsp;&nbsp;&nbsp;&nbsp;
                    <input onChange = {this.enddateChangeHandler} type="date" className="txt5 sizes robutton1" name="enddate" placeholder="MM/DD/YYY" required/>&nbsp;&nbsp;&nbsp;&nbsp;
                    <button class="txt7 sizes robutton btn btn-primary">Submit</button>

           </form>
            </div>
         );
    }
}

export default Home;
